use std::collections::HashMap;
use std::sync::LazyLock;

/// Lookup map for L2 confusion pairs by word1
pub static EN_L2_FR_CONFUSION_LOOKUP: LazyLock<HashMap<String, &'static L2ConfusionPair>> = LazyLock::new(|| {
	let mut map = HashMap::new();
	for pair in EN_L2_FR_CONFUSION_PAIRS {
		map.insert(pair.word1.to_lowercase(), pair);
	}
	map
});

/// Check if a word might be a false friend for French native speakers
pub fn get_en_l2_fr_confusion(word: &str) -> Option<&'static L2ConfusionPair> {
	EN_L2_FR_CONFUSION_LOOKUP.get(&word.to_lowercase()).copied()
}
