/// Get all incorrect examples (should trigger rules)
pub fn get_en_incorrect_examples() -> Vec<&'static PatternTestExample> {
	EN_PATTERN_TEST_EXAMPLES.iter().filter(|e| !e.is_correct).collect()
}

/// Get all correct examples (should NOT trigger rules)
pub fn get_en_correct_examples() -> Vec<&'static PatternTestExample> {
	EN_PATTERN_TEST_EXAMPLES.iter().filter(|e| e.is_correct).collect()
}

/// Get examples for a specific rule
pub fn get_en_examples_for_rule(rule_id: &str) -> Vec<&'static PatternTestExample> {
	EN_PATTERN_TEST_EXAMPLES.iter().filter(|e| e.rule_id == rule_id).collect()
}
