/// Get all redundancy rules
pub fn get_en_redundancy_rules() -> impl Iterator<Item = &'static StyleRule> {
	EN_STYLE_RULES.iter().filter(|r| r.category == StyleCategory::Redundancy)
}

/// Get all style phrases (for Aho-Corasick building)
