use std::collections::HashMap;
use std::sync::LazyLock;

/// Lookup map for word definitions
pub static EN_WORD_DEFINITION_LOOKUP: LazyLock<HashMap<String, &'static WordDefinition>> = LazyLock::new(|| {
	let mut map = HashMap::new();
	for def in EN_WORD_DEFINITIONS {
		map.insert(def.word.to_lowercase(), def);
	}
	map
});

/// Get definition for a word (case-insensitive)
pub fn get_en_word_definition(word: &str) -> Option<&'static str> {
	EN_WORD_DEFINITION_LOOKUP.get(&word.to_lowercase()).map(|d| d.definition)
}
