use std::collections::HashMap;
use std::sync::LazyLock;

/// Lookup map for compounds by non-hyphenated form
pub static FR_COMPOUND_LOOKUP: LazyLock<HashMap<String, &'static CompoundRule>> = LazyLock::new(|| {
	let mut map = HashMap::new();
	for rule in FR_COMPOUND_RULES {
		// Add lookup by joined form (without hyphen)
		let joined: String = rule.word.chars().filter(|c| *c != '-').collect();
		map.insert(joined, rule);
	}
	map
});

/// Get compound rule for a word (looks up by non-hyphenated form)
pub fn get_fr_compound(word: &str) -> Option<&'static CompoundRule> {
	FR_COMPOUND_LOOKUP.get(&word.to_lowercase()).copied()
}
