/// Get all incorrect examples (should trigger rules)
pub fn get_fr_incorrect_examples() -> Vec<&'static PatternTestExample> {
	FR_PATTERN_TEST_EXAMPLES.iter().filter(|e| !e.is_correct).collect()
}

/// Get all correct examples (should NOT trigger rules)
pub fn get_fr_correct_examples() -> Vec<&'static PatternTestExample> {
	FR_PATTERN_TEST_EXAMPLES.iter().filter(|e| e.is_correct).collect()
}

/// Get examples for a specific rule
pub fn get_fr_examples_for_rule(rule_id: &str) -> Vec<&'static PatternTestExample> {
	FR_PATTERN_TEST_EXAMPLES.iter().filter(|e| e.rule_id == rule_id).collect()
}
