/// Get synonyms for a word (binary search)
pub fn get_fr_synonyms(word: &str) -> Option<&'static [&'static str]> {
	FR_SYNONYM_RULES
		.binary_search_by_key(&word, |r| r.word)
		.ok()
		.map(|idx| FR_SYNONYM_RULES[idx].synonyms)
}
